import React, { useEffect, useState } from 'react'
import axios from 'axios'
import YouTube from 'react-youtube'
import './App.css'
import NavBar from './components/NavBar/NavBar'
import { Route, Routes } from 'react-router'
import { setUser } from './store/user'
import { useSelector } from 'react-redux'

function App() {

  const user = useSelector((state) => state.user)



  //funcion para realizar la peticion por get a la api
  const fetchMovies = async (searchKey) => {
    const type = searchKey ? 'search' : 'discover'
    const {
      data: { results },
    } = await axios.get(`${API_URL}/${type}/movie`, {
      params: {
        api_key: API_KEY,
        query: searchKey,
      },
    })
    setMovies(results)
    setMovie(results[0])

    if (results.length) {
      await fetchMovie(results[0].id)
    }
  }
  //funcion para la peticion de un solo objeto y mostrar en reproductor de video
  const fetchMovie = async (id) => {
    const { data } = await axios.get(`${API_URL}/movie/${id}`, {
      params: {
        api_key: API_KEY,
        append_to_response: 'videos',
      },
    })

    if (data.videos && data.videos.results) {
      const trailer = data.videos.results.find(
        (vid) => vid.name === 'Official Trailer',
      )
      setTrailer(trailer ? trailer : data.videos.results[0])
    }
    setMovie(data)
  }

  const selectMovie = async (movie) => {
    fetchMovie(movie.id)
    setMovie(movie)
    window.scrollTo(0, 0)
  }

  //funcion para buscar peliculas
  const searchMovies = (e) => {
    e.preventDefault()
    fetchMovies(searchKey)
  }

  useEffect(() => {
    fetchMovies()
  }, [])

  return (
    <div>
      <NavBar />
      {/* <Routes> */}
        <h2 className="text-center mt-5 mb-5">Trailer Movies</h2>
        {/* buscador */}
        <form className="container mb-4" onSubmit={searchMovies}>
          <input
            type="text"
            placeholder="search..."
            onChange={(e) => setSearchKey(e.target.value)}
          />
          <button className="btn btn-primary">Search</button>
        </form>

        {/* aca va todo el contenedor del banner y del reproductor del video */}
        <div>
          <main>
            {movie ? (
              <div
                className="viewtrailer"
                style={{
                  backgroundImage: `url("${IMAGE_PATH}${movie.backdrop_path}")`,
                }}
              >
                {playing ? (
                  <>
                    <YouTube
                      videoId={trailer.key}
                      className="reproductor container"
                      containerClassName={'youtube-container amru'}
                      opts={{
                        width: '100%',
                        height: '100%',
                        playerVars: {
                          autoplay: 1,
                          controls: 0,
                          cc_load_policy: 0,
                          fs: 0,
                          iv_load_policy: 0,
                          modestbranding: 0,
                          rel: 0,
                          showinfo: 0,
                        },
                      }}
                    />
                    <button onClick={() => setPlaying(false)} className="boton">
                      Close
                    </button>
                  </>
                ) : (
                  <div className="container">
                    <div className="">
                      {trailer ? (
                        <button
                          className="boton"
                          onClick={() => setPlaying(true)}
                          type="button"
                        >
                          Play Trailer
                        </button>
                      ) : (
                        'Sorry, no trailer available'
                      )}
                      <h1 className="text-white">{movie.title}</h1>
                      <p className="text-white">{movie.overview}</p>
                    </div>
                  </div>
                )}
              </div>
            ) : null}
          </main>
        </div>

        <div className="container mt-3">
          <div className="row">
            {movies.map((movie) => (
              <div
                key={movie.id}
                className="col-md-4 mb-3"
                onClick={() => selectMovie(movie)}
              >
                <img
                  src={`${URL_IMAGE + movie.poster_path}`}
                  alt=""
                  heigth={600}
                  width="100%"
                />
                <h4 className="text-center">{movie.title}</h4>
              </div>
            ))}
          </div>
        </div>
     {/*  </Routes> */}
    </div>
  )
}

export default App
